INSERT INTO `plants` (`plants_id`, `plants_name`, `plants_namemarket`, `plants_detail`, `plants_img`, `plantsfamily_name`, `plantsgroup_name`, `plants_timestamp`) VALUES
(116, 'Adenium Arabicum', '-', ' ยอดีเนียม อาราบิคัม, ชวนชมยักษซาอุ Adenium arabicum Balf.f. ถิ่นกําเนิด ซาอุดีอาระเบีย เยเมน ปลูกเลี้ยงในเมืองไทยมานาน แต่ไม่ทราบแน่ชัดว่าเมื่อใด เดิมเข้าใจว่าชนิดนี้คือชวนชมยักษ์ ไซโคตรานัมที่พบเฉพาะบนเกาะโซโคตรา และเรียกกันว่า โซโคตรานัม กันจนติดปากในวงกว้าง ต่อมาจึงเพิ่มคําว่า “ไทย” เข้าไปเป็น \"Thai Socotraานา\" ปัจจุบันปรับปรุงพันธุ์อย่างแพร่หลาย และตั้งชื่อตามถิ่นที่ปรับปรุงพันธุ์ เช่น ยักษ์ลพบุรี เพชรบ้านนา เพชรเมืองคง หรือตามลักษณะพิเศษ เช่น ราชินีพันดอก เป็นต้น', 'https://succulent-center.s3.amazonaws.com/plants/1127422025.jpg\r\n', 'Apocynaceae', 'Adenium', '2022-04-26 12:19:54'),
(117, 'Adenium Boehmianum', '-', 'ออดีเนียม โบเอเมียนม \r\nA. boehmianum Schinz \r\nถิ่นกําเนิด นามิเบีย แองโกลา ออดีเนียม โดฟาเรนเซ, ชวนชมโอมาน ', 'https://succulent-center.s3.amazonaws.com/plants/1374019708.jpg\r\n', 'Apocynaceae', 'Adenium', '2022-04-26 12:20:37'),
(118, 'Adenium Dhofarense', '-', '  A. dhofarense Rzepecky เป็นชนิดที่เพิ่งค้นพบเฉพาะในเขต Dhofar ทางตอนใต้ของโฮ และจัดทําคําบรรยายลักษณะเมื่อปี ค.ศ. 2015 ใบใหญ่ สีเข้มเป็นเงา และทิ้งใบในช่วงพักตัวแม่ให้น้ำอย่างสม่าเสมอ', 'https://succulent-center.s3.amazonaws.com/plants/1044907460.jpg\r\n', 'Apocynaceae', 'Adenium', '2022-04-26 12:21:24'),
(119, 'Adenium Multiflorum', '-', '  อดีเนียม มัลติฟลอรัม \r\nA. multiflorum Klotzsch \r\nถิ่นกําเนิด แอฟริกาใต้ โมซัมบิก ซิมบับเว แซมเบีย ภาพ ชนินทร์ โกรัตน์', 'https://succulent-center.s3.amazonaws.com/plants/1013361958.jpg\r\n', 'Apocynaceae', 'Adenium', '2022-04-26 12:22:06'),
(120, 'Adenium Obesum', '-', ' อดีเนียม โอเบซัม, ชวนชมฮอลแลนด์ \r\nA. obesum (Forssk.) Roem. &Schult. \r\nถิ่นกําเนิด แอฟริกาตะวันตกถึง แอฟริกาตะวันออก', 'https://succulent-center.s3.amazonaws.com/plants/911429913.jpg\r\n', 'Apocynaceae', 'Adenium', '2022-04-26 12:23:46'),
(121, 'Aloinopsis Luckhoffii', '-', ' อะลอยนอปซิส ลัคฮอฟฟิอาย\r\nA. (L.Bolus) L.Bolus\r\nถิ่นกําเนิด แอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/1094422006.jpg\r\n', 'Aizoaceae', 'Aloinopsis', '2022-04-26 12:24:32'),
(122, 'Aloinopsis Rosurata', '-', ' อะลอยนอปซิส โรซูลาตา\r\nA. rosulata (Kensit) Schwantes\r\nถิ่นกําเนิด แอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/347182795.jpg\r\n', 'Aizoaceae', 'Aloinopsis', '2022-04-26 12:25:14'),
(123, 'Aloinopsis Rubrolineata', '-', ' อะลอยนอปซิส โบรไลเนียตา\r\nA. rubrolineata (N.E.Br.) Schwantes \r\nถิ่นกําเนิด แอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/1973241140.jpg\r\n', 'Aizoaceae', 'Aloinopsis', '2022-04-26 12:26:11'),
(124, 'Aloinopsis Schooneesii', '-', ' อะลอยนอปซิส ชูวันเอสซีอาย \r\nA. Schooneesii L.Bolus \r\nถิ่นกําเนิด แอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/266797928.jpg\r\n', 'Aizoaceae', 'Aloinopsis', '2022-04-26 12:27:17'),
(125, 'Ammocharis Coranica', '-', '   แอมโมคาริส โครานิกา \r\nAmmocharis Coranica (Ker Gawl.) Herb, \r\nถิ่นกําเนิด ซิมบับเว แอฟริกาใต้ ', 'https://succulent-center.s3.amazonaws.com/plants/1403773145.jpg\r\n', 'Amaryllidaceae', 'Ammocharis', '2022-04-26 12:28:01'),
(126, 'Arguroderma PearSonii', '-', '  อาร์จีโรเดอร์มา เพียร์โซนิอาย\r\nA. (N.E.Br.) Schwantes\r\nถิ่นกำเนิด แอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/204945078.jpg\r\n', 'Aizoaceae', 'Argyroderma', '2022-04-26 12:29:16'),
(127, 'Ballyanthus Prognathus', '-', ' บัลลีแอนทัส พร็อกนาทัส \r\nBallyanthus prognathus (P.R.O.Bally) Bruyns', 'https://succulent-center.s3.amazonaws.com/plants/1340064061.jpg\r\n', 'Apocynaceae', 'Ballyanthus', '2022-04-26 12:30:08'),
(128, 'Baynesia Lophophora', '-', ' เบย์นีเซีย โลโฟโฟรา\r\nBaynesia lophophora Bruyns', 'https://succulent-center.s3.amazonaws.com/plants/1802517542.jpg\r\n', 'Apocynaceae', 'Baynesia', '2022-04-26 12:31:17'),
(129, 'Bergeranthus Multiceps', '-', '  เบอร์กีแรนทัส มัลติเซ็ปส์, ม้าอ่อน \r\nBergeranthus multiceps (Salm-Dyck) Schwantes \r\nถิ่นกําเนิด แอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/976309694.jpg\r\n', 'Aizoaceae', 'Bergeranthus', '2022-04-26 12:31:57'),
(130, 'Bergeranthus Vespertinus', '-', ' เบอร์กีแรนทัส เวสเปอร์ที่นัส, ม้านิ่ม\r\nB. vespertinus (Berger) Schwantes \r\nถิ่นกําเนิด แอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/422781583.jpg\r\n', 'Aizoaceae', 'Bergeranthus', '2022-04-26 12:32:36'),
(131, 'Bljlia Cana', '-', ' บิจเลีย คานา \r\nBljlia cana (Haw.) N.E.Br.\r\nพบเฉพาะบริเวณแคบ ๆ ในประเทศแอฟริกาใต้', 'https://succulent-center.s3.amazonaws.com/plants/720076524.jpg\r\n', 'Aizoaceae', 'Bijlia', '2022-04-26 12:33:12'),
(132, 'Boophane Disticha', '-', 'บูเฟน ดิสติชา \r\nBoophane disticha (L.f.)', 'https://succulent-center.s3.amazonaws.com/plants/1185117242.jpg\r\n', 'Amaryllidaceae', 'Boophane', '2022-04-26 12:34:01'),
(133, 'Braunsia Apiculata', '-', '   บรานเซีย เอพีคูลาตา\r\nBraunsia Apiculata (Kensit) L.Bolus\r\nพบเฉพาะในประเทศแอฟริกาใต้เท่านั้น', 'https://succulent-center.s3.amazonaws.com/plants/291740527.jpg\r\n', 'Aizoaceae', 'Braunsia', '2022-04-26 12:56:36'),
(134, 'Braunsia Maximillan', '-', '  บรานเซีย แม็กซิมิเลียน\r\nB, maximilian (Schltr. & Berger) Schwantes\r\nแอฟริกาใต้\r\n', 'https://succulent-center.s3.amazonaws.com/plants/923580934.jpg\r\n', 'Aizoaceae', 'Braunsia', '2022-04-26 12:57:28'),
